import React, { useState, useEffect } from "react";
import Hero from "./Hero";
import About from "./About";
import Gift from "./Gift";
import Statues from "./Statues";
import WonderfullGift from "./WonderfullGift";
import Testimolina from "./Testimolina";
import OurTeam from "./OurTeam";
import Newsletter from "./Newsletter";
import Footer from "./Footer";

const App = () => {
  // Manage dark mode state
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isScroll, setIsScroll] = useState(false);

  useEffect(() => {
    // Retrieve and apply the user's theme preference from localStorage
    const storedTheme = localStorage.getItem("theme");
    if (storedTheme === "dark") {
      setIsDarkMode(true);
      document.documentElement.classList.add("dark");
    }
  }, []);

  // Handle theme change
  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode);
    if (!isDarkMode) {
      document.documentElement.classList.add("dark");
      localStorage.setItem("theme", "dark");
    } else {
      document.documentElement.classList.remove("dark");
      localStorage.setItem("theme", "light");
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      setIsScroll(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const GoToUp = () => window.scrollTo({ top: 0, behavior: "smooth" });

  return (
    <main className="font-Poppins dark:bg-neutral-900 dark:text-white">
      <button
        className={`${
          isScroll ? "block" : "hidden"
        } transition-all px-4 py-2 rounded-full bg-neutral-800 text-white fixed bottom-4 right-3 z-40 text-lg`}
        onClick={GoToUp}
      >
        <i className="fa-solid fa-arrow-up"></i>
      </button>
      <Hero toggleDarkMode={toggleDarkMode} isDarkMode={isDarkMode} />
      <About />
      <Gift />
      <Statues />
      <WonderfullGift />
      <Testimolina />
      <OurTeam />
      <Newsletter />
      <Footer />
    </main>
  );
};

export default App;
